import React, { useState, useEffect } from 'react'
import { useLocation } from 'react-router-dom'
import axios from 'axios'

const PhotoView = () => {
const location = useLocation()
  // console.log("location:", location)
  // console.log("location.state:", location.state.albumID)
  const [photo_id, setPhotoID] = useState(location.state.photoID)
  console.log("photo_id:",photo_id)
  const [photo, setPhoto] = useState([])
  const [albums, setAlbums] = useState([])
  const [users, setUsers] = useState([])

  useEffect(()=>{
    const fetchAllPhotos = async () =>{
      try {
        const res = await axios.get("http://localhost:8800/photos")
        // console.log(res.data)
		for (let  i = 0; i<res.data.length; i++){
			// console.log("in for")
			if(photo_id==res.data[i].photo_id){
				// console.log("res.data",res.data)
				setPhoto(res.data[i])
				console.log(photo)
				break
			}
		}
        //setPhotos(res.data)
      } catch (error) {
        console.log(error)       
      }
    }
    fetchAllPhotos()
  })

  const getUserId = async () => {
	console.log("in getUserId")
	try {
		const res = await axios.get("http://localhost:8800/albums")
		console.log("albums",res.data)
		setAlbums(res.data)

		res = await axios.get("http://localhost:8800/users")
		console.log("users",res.data)
		setUsers(res.data)

		for (let  i = 0; i<albums.length; i++) {

		}
	} catch (error) {
		console.log(error)       
	}

	// return(
	// 	<div>yo</div>
	// )
  }
  return (
    <div>
		<h1>Photo</h1>
		<div>
			<span>{photo.data}</span>
			<p>Posted by: {getUserId()}</p>
		</div>
		<img src="https://example.com/photo.jpg" alt="A beautiful photo"/>
		<p>Posted by: XYZ</p>
		<p>2 likes</p>
		
		
		<h2>Leave a Comment</h2>
		<form>
			<label for="name">Name:</label>
			<input type="text" id="name" name="name"/><br/><br/>
			
			<label for="comment">Comment:</label><br/>
			<textarea id="comment" name="comment" rows="4" cols="50"></textarea><br/><br/>
			
			<input type="submit" value="Submit"/>
		</form>
		
		<h2>Comments</h2>
		<div id="comments">
			<div class="comment">
				<h3>John Doe</h3>
				<p>This is a great photo!</p>
			</div>
			<div class="comment">
				<h3>Jane Smith</h3>
				<p>Wow, I love this picture!</p>
			</div>
		</div>
    </div>
  )
}

export default PhotoView
