import React, { useState, useEffect } from 'react'
import Search from './components/Search'
import axios from 'axios'
import{ Link } from "react-router-dom";

const Profile = () => {
  const [albums, setAlbums] = useState([])
  const [user_id, setUserId] = useState("jackie_chan") //need to set correct userid based on who logs in
  const [showForm, setShowForm] = useState(false)
  const [album, setAlbum] = useState({
    album_name:"",
    user_id: user_id,
    date_of_creation: new Date().toUTCString()
  });

  useEffect(()=>{
    const fetchAllAlbums = async () =>{
      try {
        const res = await axios.get("http://localhost:8800/albums")
        console.log(res)
        setAlbums(res.data)
      } catch (error) {
        console.log(error)       
      }
    }
    fetchAllAlbums()

    //Need to put logic to change user_id based on user login
    setUserId("jackie_chan")
  })

  const handleNewAlbumClick = (e) => {
    setShowForm(true)
  }

  const handleClickSave = async (e) =>{
    //e.preventDefault()
    try {
      console.log(album)
      await axios.post("http://localhost:8800/albums", album)
      setShowForm(false)
      
    } catch (error) {
      
    }

  }

  const renderForm = (e) => {
    const handleChange = (e) => {
      console.log(user_id)
      setAlbum(prev=>({...prev, [e.target.name]: e.target.value}))
      //setAlbum(user_id=user_id)
    };
    console.log(album)
    //console.log(showForm)
    return(
    <div className='form'>
      <h3>Create New Album</h3>
      <input type='text' placeholder='Album Name' onChange={handleChange} name='album_name'/>
      <button onClick={handleClickSave}>Save</button>
    </div>
    )
  }

  const handleAlbumClick = async (id) =>{
    //e.preventDefault()
    try {
      <Link to={{
        pathname: "/album-content",
        state: id
      }} />
      
    } catch (error) {
      
    }

  }

  return (
    <div>
        <Search/>
        <div>
            <img src="profile_photo.jpg" alt="Profile Photo"/>
            <h1>First Name</h1>
            <p className='user-id'>{user_id}</p>
            <p>Date of Birth: January 1, 2000</p>
            <p>Email: user@example.com</p>
            <p>Gender: Female</p>
            <p>Hometown: City, Country</p>
            <button>Followers</button>
        </div>
        <hr />
        <div className='albums'>
          {/* instead of photos, make link for album (using album names) and then on clicking album, take to page to display all photos in that album. once photo is clicked show PhotoView */}
            <h2>Albums</h2> 
            {albums.map(album =>(
              <div className='album' key={album.album_id}>
                <h3> 
                  {console.log("album_id:", album.album_id)}
                  <Link to={"/album-content"} state={{ albumID:album.album_id, albumName:album.album_name}}>
                    {album.user_id==user_id && album.album_name}
                  </Link>
                </h3>
              </div>
              
            ))}
        </div>
        <div>
        <button onClick={handleNewAlbumClick}>
          + New Album
        </button>
        {showForm && renderForm()}
        </div>
    </div>
  )
}

export default Profile
