import React, { useState, useEffect } from 'react'
import { useLocation, Link, useNavigate } from 'react-router-dom'
import axios from 'axios'

const UploadImage = () => {
  const location = useLocation()
  // console.log("location:", location)
  // console.log("location.state:", location.state.albumID)
  const [album_id, setAlbumID] = useState(location.state.albumID)
  const [photo, setPhoto] = useState({
    album_id:album_id,
    caption:"",
    data:null
  });
  const [tag, setTag] = useState({
    photo_id:null,
    text:""
  });

  const navigate = useNavigate()

  const handleChange = (e) => {
    setPhoto((prev) => ({...prev, [e.target.name]: e.target.value}));
    setTag((prev) => ({...prev, [e.target.name]: e.target.value}));
  };
  console.log(photo)
  console.log(tag)

  const handleClick = async e =>{
    e.preventDefault()
    try {
      await axios.post("http://localhost:8800/photos", photo)
      await axios.post("http://localhost:8800/tags", tag)
      navigate("/profile")  
    } catch (error) {
      console.log(error)      
    }
  }
  return (
    <div>
        <h1>Upload Photos</h1>

       {/* <form method="POST" action="/upload" enctype="multipart/form-data"> */}
            <label for="file-upload">Choose a file to upload:</label>
            <input type="file" id="file-upload" onChange={handleChange} name="data"/><br/><br/>

            <label for="caption">Caption:</label>
            <input type="text" id="caption" onChange={handleChange} name="caption" placeholder="Enter your caption here..."/><br/><br/>

            <label for="tags">Tag other accounts:</label>
            <input type="text" id="tags" onChange={handleChange} name="text" placeholder="Enter account usernames separated by commas..."/><br/><br/>

            <button onClick={handleClick}>Upload Photo</button>
        {/* </form> */}
    </div>
  )
}

export default UploadImage
